const REVIEW_PHRASES = [
    { text: "Absolutely essential for my workflow.", rating: [5] },
    { text: "Great tool, but the learning curve is steep.", rating: [4, 3] },
    { text: "Changed the way I work completely. Highly recommended!", rating: [5] },
