import { NextRequest, NextResponse } from 'next/server';
import { createAdminClient } from '@/lib/supabase/admin';

export async function GET(request: NextRequest) {
    try {
        const supabase = createAdminClient();

        const { data, error } = await supabase
            .from('newsletter_subscribers')
            .select('email, source, status, created_at')
            .order('created_at', { ascending: false });

        if (error) throw error;

        // Convert to CSV
        const headers = ['Email', 'Source', 'Status', 'Subscribed At'];
        const rows = (data || []).map(sub => [
            sub.email,
            sub.source || 'n/a',
            sub.status,
            new Date(sub.created_at).toISOString()
        ]);

        const csvContent = [
            headers.join(','),
            ...rows.map(row => row.join(','))
        ].join('\n');

        return new NextResponse(csvContent, {
            headers: {
                'Content-Type': 'text/csv',
                'Content-Disposition': `attachment; filename=subscribers-${new Date().toISOString().split('T')[0]}.csv`
            }
        });

    } catch (error: any) {
        return NextResponse.json({ error: error.message }, { status: 500 });
    }
}

import { NextRequest, NextResponse } from 'next/server';
import { createAdminClient } from '@/lib/supabase/admin';
import { ItemStatus } from '@/types/models';
import { sendEmail } from '@/lib/email';

export async function POST(request: NextRequest) {
    try {
        const body = (await request.json()) as { id: string; action: string; tags?: string[] };
        const { id, action } = body;

        if (!id || !['approve', 'reject'].includes(action)) {
            return NextResponse.json({ error: 'Invalid parameters' }, { status: 400 });
        }

        const supabase = createAdminClient() as any;
        const status = (action === 'approve' ? 'approved' : 'rejected') as ItemStatus;

        const { error } = await supabase
            .from('items')
            .update({ status })
            .eq('id', id);

        if (error) {
            console.error('Admin tool action error:', error);
            return NextResponse.json({ error: error.message }, { status: 500 });
        }

        if (status === 'approved' && body.tags && Array.isArray(body.tags)) {
            const tagInserts = body.tags.map((tagId: string) => ({
                item_id: id,
                tag_id: tagId
            }));

            if (tagInserts.length > 0) {
                // Remove existing tags first to be safe (though for pending items usually none)
                await supabase.from('item_tags').delete().eq('item_id', id);

                const { error: tagError } = await supabase
                    .from('item_tags')
                    .insert(tagInserts);

                if (tagError) {
                    console.error('Error adding tags:', tagError);
                    // Don't fail the whole request, but log it
                }
            }
        }

        // Email Notifications
        const { data: item } = await supabase
            .from('items')
            .select('submitter_email, name')
            .eq('id', id)
            .single();

        if (item?.submitter_email) {
            if (status === 'approved') {
                await sendEmail({
                    to: item.submitter_email,
                    subject: `Asset Approved: ${item.name} is now live!`,
                    template: 'item_approved',
                    data: { name: item.name }
                });
            } else if (status === 'rejected') {
                await sendEmail({
                    to: item.submitter_email,
                    subject: `Status Update: Submissions regarding ${item.name}`,
                    template: 'item_rejected',
                    data: { name: item.name }
                });
            }
        }

        return NextResponse.json({ success: true, status });
    } catch (error) {
        console.error('API Error:', error);
        return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
    }
}

import { createAdminClient } from "@/lib/supabase/admin";
import { NextResponse } from "next/server";

export async function GET() {
    try {
        const supabase = createAdminClient();

        // 1. Get raw users from auth schema (requires service role with access to auth schema)
        // Note: Supabase JS client doesn't expose auth.users table directly easily.
        // Usually, the best practice is to have a public.users table synced via triggers.
        // However, we can use the admin.auth.listUsers() API if we have the service role key.

        const { data: { users }, error: usersError } = await supabase.auth.admin.listUsers();

        if (usersError) throw usersError;

        // 2. Enhance user data with activity stats (votes, submissions)
        // We'll do this by querying votes and items and aggregating by user_id
        const userIds = users.map(u => u.id);

        if (userIds.length === 0) {
            return NextResponse.json({ users: [] });
        }

        // Get submission counts
        const { data: submissions } = await supabase
            .from('items')
            .select('user_id')
            .in('user_id', userIds);

        // Get vote counts
        const { data: votes } = await supabase
            .from('votes')
            .select('user_id')
            .in('user_id', userIds);

        // Map counts
        const submissionCounts = (submissions || []).reduce((acc: any, item: any) => {
            acc[item.user_id] = (acc[item.user_id] || 0) + 1;
            return acc;
        }, {});

        const voteCounts = (votes || []).reduce((acc: any, vote: any) => {
            acc[vote.user_id] = (acc[vote.user_id] || 0) + 1;
            return acc;
        }, {});

        // Format response
        const enhancedUsers = users.map(u => ({
            id: u.id,
            email: u.email,
            created_at: u.created_at,
            last_sign_in_at: u.last_sign_in_at,
            submission_count: submissionCounts[u.id] || 0,
            vote_count: voteCounts[u.id] || 0,
            role: u.user_metadata?.role || 'user' // Assuming metadata or just default
        }));

        // Sort by most recent sign in
        enhancedUsers.sort((a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime());

        return NextResponse.json({ users: enhancedUsers });

    } catch (error) {
        console.error('Error fetching admin users:', error);
        return NextResponse.json({ error: 'Failed to fetch users' }, { status: 500 });
    }
}

