                                if (category.slug === 'saas-essentials') return <Rocket size={size} strokeWidth={strokeWidth} />;
                                return <Wrench size={size} strokeWidth={strokeWidth} />;
                            })()}
                        </div>
                        <div>
                            <div className="flex items-center gap-3 mb-2">
