'use client';

import { useState } from 'react';
import { Review } from '@/types/models';
import { StarRating } from '@/components/StarRating';
import { ReviewHelpfulButton } from '@/components/ReviewHelpfulButton';
import { Inbox, ChevronDown, ChevronUp } from 'lucide-react';
import { cn } from '@/lib/utils';

interface ReviewListProps {
    reviews: Review[];
}

export function ReviewList({ reviews }: ReviewListProps) {
    const [isExpanded, setIsExpanded] = useState(false);

    // Sort logic handled by parent (server) query, but we assume they are passed in correct order.
    // Display logic: show 4 initially, or all if expanded.
    const displayedReviews = isExpanded ? reviews : reviews.slice(0, 4);
    const hasMore = reviews.length > 4;

    if (reviews.length === 0) {
        return (
            <div className="rounded-[3rem] border border-dashed border-slate-200 dark:border-slate-800 bg-slate-50/50 dark:bg-slate-900/30 p-24 text-center">
                <div className="mx-auto flex h-20 w-20 items-center justify-center rounded-[2rem] bg-slate-100 dark:bg-slate-800 text-slate-300 dark:text-slate-700 mb-8">
                    <Inbox size={40} strokeWidth={1} />
                </div>
                <h4 className="text-2xl font-black text-slate-900 dark:text-white uppercase tracking-tight mb-2">No signals detected</h4>
                <p className="text-sm text-slate-500 dark:text-slate-400 font-medium max-w-xs mx-auto">Be the first to record a tactical review for this asset.</p>
            </div>
        );
    }

    return (
        <div className="space-y-12">
            <div className="grid gap-8">
                {displayedReviews.map((review) => (
                    <div key={review.id} className="group rounded-[2.5rem] border border-slate-200 dark:border-slate-800 bg-white dark:bg-slate-900/50 p-10 backdrop-blur-sm shadow-sm hover:shadow-2xl hover:border-blue-500/20 transition-all duration-500 animate-in fade-in slide-in-from-bottom-4">
                        <div className="flex justify-between items-start mb-8">
                            <div className="flex items-center gap-5">
                                <div className="h-14 w-14 rounded-2xl bg-slate-100 dark:bg-slate-800 flex items-center justify-center text-slate-900 dark:text-white font-black text-xs border border-slate-200 dark:border-slate-700">
                                    {review.session_id.substring(0, 2).toUpperCase()}
                                </div>
                                <div>
                                    <div className="flex items-center gap-3 mb-1">
                                        <div className="text-[12px] font-black text-slate-900 dark:text-white uppercase tracking-widest">Verified Auditor</div>
                                        <div className="h-1 w-1 rounded-full bg-slate-300 dark:bg-slate-700" />
                                        <div className="text-[10px] font-black text-slate-400 uppercase tracking-widest">
                                            TX_{review.id.split('-')[0]}
                                        </div>
                                    </div>
                                    <StarRating rating={review.rating} size="sm" />
                                </div>
                            </div>
                            <div className="flex flex-col items-end gap-2 text-right">
                                <div className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">
                                    {new Date(review.created_at).toLocaleDateString()}
                                </div>
                                <ReviewHelpfulButton
                                    reviewId={review.id}
                                    initialCount={review.helpful_count || 0}
                                    className="text-[9px] font-black uppercase tracking-widest text-blue-600 hover:text-blue-700 flex items-center gap-1"
                                />
                            </div>
                        </div>
                        <p className="text-slate-600 dark:text-slate-300 italic leading-relaxed text-xl font-medium relative">
                            <span className="absolute -left-4 top-0 text-slate-100 dark:text-slate-800 text-6xl leading-none select-none font-serif">&quot;</span>
                            <span className="relative z-10">{review.comment}</span>
                        </p>
                    </div>
                ))}
            </div>

            {hasMore && (
                <div className="flex justify-center">
                    <button
                        onClick={() => setIsExpanded(!isExpanded)}
                        className="group flex items-center gap-3 px-8 py-4 rounded-2xl bg-white dark:bg-slate-900 border border-slate-200 dark:border-slate-800 text-slate-900 dark:text-white font-black uppercase tracking-widest text-[10px] hover:border-blue-500/50 hover:shadow-lg transition-all"
                    >
                        {isExpanded ? (
                            <>
                                Show Less Signals <ChevronUp size={14} />
                            </>
                        ) : (
                            <>
                                View All Signals <ChevronDown size={14} className="group-hover:translate-y-0.5 transition-transform" />
                            </>
                        )}
                    </button>
                </div>
            )}
        </div>
    );
}

'use client';

import { useState, useEffect, useRef, Suspense } from 'react';
import { useRouter, useSearchParams, usePathname } from 'next/navigation';
import Link from 'next/link';
import Image from 'next/image';
import { Search, Loader2, Star } from 'lucide-react';
import { ItemWithDetails } from '@/types/models';
import { getLogoUrl } from '@/lib/utils';
import { ToolIcon } from '@/components/ToolIcon';

function SearchBarContent() {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState<ItemWithDetails[]>([]);
    const [isSearching, setIsSearching] = useState(false);
    const [showDropdown, setShowDropdown] = useState(false);
    const dropdownRef = useRef<HTMLDivElement>(null);
    const inputRef = useRef<HTMLInputElement>(null);
    const router = useRouter();
    const searchParams = useSearchParams();
    const pathname = usePathname();

    useEffect(() => {
        const handleKeyDown = (e: KeyboardEvent) => {
            if ((e.metaKey || e.ctrlKey) && e.key === 'k') {
                e.preventDefault();
                inputRef.current?.focus();
            }
        };

        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, []);

    useEffect(() => {
        const handleClickOutside = (event: MouseEvent) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
                setShowDropdown(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    useEffect(() => {
        const fetchResults = async () => {
            if (query.length < 2) {
                setResults([]);
                setShowDropdown(false);
                return;
            }

            setIsSearching(true);
            try {
                const response = await fetch(`/api/search?q=${encodeURIComponent(query)}`);
                const data = await response.json();
                if (data.results) {
                    setResults(data.results);
                    setShowDropdown(true);
                }
            } catch (error) {
                console.error('Search error:', error);
            } finally {
                setIsSearching(false);
            }
        };

        const timeoutId = setTimeout(fetchResults, 300);
        return () => clearTimeout(timeoutId);
    }, [query]);

    // Pre-fill query from URL if we are on the search page
    useEffect(() => {
        if (pathname === '/search') {
            const q = searchParams.get('q');
            if (q) setQuery(q);
        }
    }, [pathname, searchParams]);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (query.trim()) {
            setShowDropdown(false);

            // If we are already on the search page, preserve current filters
            if (pathname === '/search') {
                const params = new URLSearchParams(searchParams.toString());
                params.set('q', query.trim());
                router.push(`/search?${params.toString()}`);
            } else {
                router.push(`/search?q=${encodeURIComponent(query.trim())}`);
            }
        }
    };

    return (
        <div className="relative w-full max-w-xl mx-auto" ref={dropdownRef}>
            <form onSubmit={handleSubmit} className="relative group">
                <input
                    ref={inputRef}
                    type="text"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    onFocus={() => query.length >= 2 && setShowDropdown(true)}
                    placeholder="Search for AI writing tools, generators..."
                    className="w-full rounded-2xl border border-slate-200 dark:border-slate-800 bg-white/50 dark:bg-slate-900/50 px-6 py-4 pl-12 text-slate-900 dark:text-white placeholder-slate-400 backdrop-blur-xl focus:border-blue-500/50 focus:outline-none focus:ring-4 focus:ring-blue-500/10 transition-all shadow-xl shadow-blue-500/5 font-medium group-hover:border-slate-300 dark:group-hover:border-slate-700"
                />
                <div className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-blue-500 transition-colors">
                    <Search size={18} strokeWidth={2.5} />
                </div>
                <div className="absolute right-4 top-1/2 -translate-y-1/2 flex items-center gap-2">
                    {isSearching ? (
                        <Loader2 className="animate-spin h-4 w-4 text-blue-500" />
                    ) : (
                        <div className="hidden sm:flex items-center gap-1 font-black text-[10px] text-slate-400 dark:text-slate-500 bg-slate-100 dark:bg-slate-800 px-2 py-1 rounded-lg border border-slate-200 dark:border-slate-700">
                            <span className="text-[12px]">Γîÿ</span>
                            <span>K</span>
                        </div>
                    )}
                </div>
            </form>

            {showDropdown && results.length > 0 && (
                <div className="absolute top-full left-0 right-0 mt-3 rounded-[2rem] border border-slate-200 dark:border-slate-800 bg-white/95 dark:bg-slate-900 shadow-2xl overflow-hidden z-50 backdrop-blur-xl animate-in fade-in slide-in-from-top-2">
                    <div className="max-h-[400px] overflow-y-auto custom-scrollbar">
                        {results.map((result) => (
                            <Link
                                key={result.id}
                                href={`/tool/${result.slug}`}
                                onClick={() => setShowDropdown(false)}
                                className="flex items-center gap-4 px-6 py-4 hover:bg-slate-50 dark:hover:bg-slate-800 transition-colors border-b border-slate-100 dark:border-slate-800 last:border-0 group/result"
                            >
                                <div className="h-12 w-12 flex-shrink-0 rounded-xl bg-slate-100 dark:bg-slate-800 border border-slate-200 dark:border-slate-700 overflow-hidden p-2 group-hover/result:scale-105 transition-transform">
                                    <ToolIcon
                                        url={result.logo_url}
                                        name={result.name}
                                        websiteUrl={result.website_url}
                                        width={48}
                                        height={48}
                                        unoptimized={true}
                                        className="h-full w-full"
                                        imgClassName="object-contain"
                                    />
                                </div>
                                <div className="flex-1">
                                    <div className="font-black text-slate-900 dark:text-white group-hover/result:text-blue-600 dark:group-hover/result:text-blue-400 transition-colors uppercase tracking-tight leading-none">{result.name}</div>
                                    <div className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-1">{result.categories?.name}</div>
                                </div>
                                <div className="flex flex-col items-end gap-1">
                                    <div className="flex items-center gap-1 text-sm font-black text-slate-900 dark:text-white">
                                        <Star size={12} className="text-yellow-400 fill-yellow-400" />
                                        {(result.average_rating || 0).toFixed(1)}
                                    </div>
                                    <div className="text-[9px] font-black text-slate-400 uppercase tracking-widest">Score: {result.score}</div>
                                </div>
                            </Link>
                        ))}
                    </div>
                </div>
            )}

            {showDropdown && query.length >= 2 && results.length === 0 && !isSearching && (
                <div className="absolute top-full left-0 right-0 mt-3 rounded-2xl border border-slate-200 dark:border-slate-800 bg-white dark:bg-slate-900 p-8 text-center text-slate-500 z-50 shadow-2xl font-bold italic">
                    No elite tools found for &quot;{query}&quot;
                </div>
            )}
        </div>
    );
}

export function SearchBar() {
    return (
        <Suspense fallback={<div className="w-full max-w-xl mx-auto h-14 rounded-2xl bg-slate-100 dark:bg-slate-800/50 animate-pulse" />}>
            <SearchBarContent />
        </Suspense>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { Share2, Twitter, Linkedin, Link as LinkIcon, Check } from 'lucide-react';
import { cn } from '@/lib/utils';

interface ShareButtonsProps {
    url: string;
    title: string;
    className?: string;
}

export function ShareButtons({ url, title, className }: ShareButtonsProps) {
    const [copied, setCopied] = useState(false);
    const [shareUrl, setShareUrl] = useState(url);

    useEffect(() => {
        setShareUrl(`${window.location.origin}${url}`);
    }, [url]);

    const encodedUrl = encodeURIComponent(shareUrl);
    const encodedTitle = encodeURIComponent(title);

    const copyToClipboard = () => {
        navigator.clipboard.writeText(shareUrl);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
    };

    const links = [
        {
            name: 'X',
            icon: Twitter,
            href: `https://twitter.com/intent/tweet?text=${encodedTitle}&url=${encodedUrl}`,
            color: 'hover:bg-slate-900 hover:text-white dark:hover:bg-white dark:hover:text-black'
        },
        {
            name: 'LinkedIn',
            icon: Linkedin,
            href: `https://www.linkedin.com/sharing/share-offsite/?url=${encodedUrl}`,
            color: 'hover:bg-[#0077b5] hover:text-white'
        }
    ];

    return (
        <div className={cn("flex items-center gap-3", className)}>
            <div className="flex -space-x-1">
                {links.map((link) => (
                    <a
                        key={link.name}
                        href={link.href}
                        target="_blank"
                        rel="noopener noreferrer"
                        className={cn(
                            "flex h-10 w-10 items-center justify-center rounded-full bg-white dark:bg-slate-900 border border-slate-200 dark:border-slate-800 text-slate-500 transition-all",
                            link.color
                        )}
                        title={`Share on ${link.name}`}
                    >
                        <link.icon size={16} />
                    </a>
                ))}
            </div>

            <button
                onClick={copyToClipboard}
                className={cn(
                    "flex h-10 px-4 items-center gap-2 rounded-full bg-white dark:bg-slate-900 border border-slate-200 dark:border-slate-800 text-[10px] font-black uppercase tracking-widest text-slate-500 transition-all",
                    copied ? "text-green-600 border-green-500/30 bg-green-50 dark:bg-green-900/10" : "hover:border-blue-500/30 hover:text-blue-600"
                )}
            >
                {copied ? (
                    <>
                        <Check size={14} />
                        Copied
                    </>
                ) : (
                    <>
                        <LinkIcon size={14} />
                        Copy Analysis URL
                    </>
                )}
            </button>
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { createPortal } from 'react-dom';
import {
    X,
    CheckCircle2,
    AlertCircle,
    Plus,
    Globe,
    Layout,
    FileText,
    Loader2,
    Sparkles,
    CreditCard,
    ArrowLeft,
    ShieldCheck
} from 'lucide-react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";

interface Category {
    id: string;
    name: string;
    slug: string;
    description: string;
}

interface SubmitToolModalProps {
    className?: string;
    children?: React.ReactNode;
}

export function SubmitToolModal({ className, children }: SubmitToolModalProps) {
    const [isOpen, setIsOpen] = useState(false);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [submitStatus, setSubmitStatus] = useState<'idle' | 'success' | 'error'>('idle');
    const [errorMessage, setErrorMessage] = useState('');
    const [categories, setCategories] = useState<Category[]>([]);
    const [isLoadingCategories, setIsLoadingCategories] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        website_url: '',
        description: '',
        category: '',
        logo_url: '',
        submitter_email: '',
    });
    const [formErrors, setFormErrors] = useState<Record<string, string>>({});
    const [mounted, setMounted] = useState(false);
    const [step, setStep] = useState<'form' | 'payment'>('form');
    const [transactionId, setTransactionId] = useState<string | null>(null);

    useEffect(() => {
        setMounted(true);
    }, []);

    // Fetch categories when modal opens
    useEffect(() => {
        if (isOpen && categories.length === 0) {
            fetchCategories();
        }
    }, [isOpen, categories.length]);

    const fetchCategories = async () => {
        setIsLoadingCategories(true);
        try {
            const response = await fetch('/api/categories');
            if (response.ok) {
                const data = await response.json();
                setCategories(data.categories || []);
                if (data.categories?.length > 0) {
                    setFormData(prev => ({ ...prev, category: data.categories[0].slug }));
                }
            }
        } catch (error) {
            console.error('Failed to fetch categories:', categories);
        } finally {
            setIsLoadingCategories(false);
        }
    };

    const validateForm = () => {
        const errors: Record<string, string> = {};

        if (!formData.name.trim()) {
            errors.name = 'Tool name is required';
        } else if (formData.name.length < 2) {
            errors.name = 'Tool name must be at least 2 characters';
        } else if (formData.name.length > 100) {
            errors.name = 'Tool name must be less than 100 characters';
        }

        if (!formData.website_url.trim()) {
            errors.website_url = 'Website URL is required';
        } else {
            try {
                const url = new URL(formData.website_url);
                if (!['http:', 'https:'].includes(url.protocol)) {
                    errors.website_url = 'URL must start with http:// or https://';
                }
            } catch {
                errors.website_url = 'Please enter a valid URL';
            }
        }

        if (!formData.category) {
            errors.category = 'Please select a category';
        }

        if (formData.description && formData.description.length > 500) {
            errors.description = 'Description must be less than 500 characters';
        }

        if (formData.submitter_email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.submitter_email)) {
            errors.submitter_email = 'Please enter a valid email address';
        }

        setFormErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const handleProceedToPayment = (e: React.FormEvent) => {
        e.preventDefault();
        if (validateForm()) {
            setStep('payment');
        }
    };

    const handleSubmit = async (txId?: string) => {
        if (!validateForm()) { // Should be valid already, but re-checked
            return;
        }

        setIsSubmitting(true);
        setSubmitStatus('idle');
        setErrorMessage('');

        try {
            const payload = {
                ...formData,
                transaction_id: txId || transactionId,
                payment_amount: 2.00,
                payment_status: 'paid'
            };

            const response = await fetch('/api/submit-tool', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(payload),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Submission failed');
            }

            setSubmitStatus('success');
            setTimeout(() => {
                setIsOpen(false);
                setFormData({ name: '', website_url: '', description: '', category: categories[0]?.slug || '', logo_url: '', submitter_email: '' });
                setStep('form');
                setTransactionId(null); // Explicit reset
                setSubmitStatus('idle');
                setFormErrors({});
            }, 2000);
        } catch (error) {
            console.error('Submit error:', error);
            setSubmitStatus('error');
            setErrorMessage(error instanceof Error ? error.message : 'Failed to submit tool');
        } finally {
            setIsSubmitting(false);
        }
    };

    const handlePaymentSuccess = (details: any) => {
        const txId = details.id || 'PAYPAL_TX_' + Date.now();
        setTransactionId(txId);
        handleSubmit(txId);
    };

    const handleInputChange = (field: string, value: string) => {
        setFormData(prev => ({ ...prev, [field]: value }));
        // Clear error for this field when user starts typing
        if (formErrors[field]) {
            setFormErrors(prev => {
                const newErrors = { ...prev };
                delete newErrors[field];
                return newErrors;
            });
        }
    };

    if (!mounted) return (
        <button
            onClick={() => setIsOpen(true)}
            className={className || "group inline-flex items-center gap-2 rounded-xl bg-slate-900 px-6 py-3 text-sm font-black text-white hover:bg-slate-800 transition-all uppercase tracking-widest"}
        >
            <Plus size={16} />
            {children || "Submit Tool"}
        </button>
    );

    return (
        <>
            <button
                onClick={() => setIsOpen(true)}
                className={className || "group inline-flex items-center gap-2 rounded-xl bg-slate-900 dark:bg-blue-600 px-6 py-3 text-sm font-black text-white hover:bg-slate-800 dark:hover:bg-blue-500 transition-all shadow-xl shadow-blue-500/10 active:scale-95 uppercase tracking-widest"}
