'use client';

import { useState, useEffect } from 'react';
import Image from 'next/image';
import { getLogoUrl } from '@/lib/utils';
import { cn } from '@/lib/utils';

interface ToolIconProps {
    url?: string | null;
    name: string;
    websiteUrl?: string | null;
    className?: string;
    imgClassName?: string;
    width?: number;
    height?: number;
    fill?: boolean;
    priority?: boolean;
    unoptimized?: boolean;
    sizes?: string;
}

export function ToolIcon({
    url,
    name,
    websiteUrl,
    className,
    imgClassName,
    width,
    height,
    fill = false,
    priority = false,
    unoptimized = true,
    sizes
}: ToolIconProps) {
    const [src, setSrc] = useState<string>(getLogoUrl(url, name, websiteUrl));
    const [errorCount, setErrorCount] = useState(0);

    useEffect(() => {
        setSrc(getLogoUrl(url, name, websiteUrl));
        setErrorCount(0);
    }, [url, name, websiteUrl]);

    const handleError = () => {
        if (errorCount === 0) {
            // If the current src is already a google favicon and it failed, skip to final fallback
            const isGoogle = src.includes('google.com') || src.includes('gstatic.com');

            if (isGoogle) {
                setErrorCount(2);
                setSrc(getLogoUrl(null, name, null));
                return;
            }

            // Otherwise, try Google Favicon as step 2
            setErrorCount(1);
            if (websiteUrl) {
                try {
                    const urlObj = new URL(websiteUrl.startsWith('http') ? websiteUrl : `https://${websiteUrl}`);
                    const domain = urlObj.hostname.replace('www.', '');
                    setSrc(`https://www.google.com/s2/favicons?domain=${domain}&sz=128`);
                } catch {
                    setErrorCount(2);
                    setSrc(getLogoUrl(null, name, null));
                }
            } else {
                setErrorCount(2);
                setSrc(getLogoUrl(null, name, null));
            }
        } else {
            // Final fallback to UI Avatars
            setErrorCount(2);
            setSrc(getLogoUrl(null, name, null));
        }
    };

    const isFill = fill || (!width && !height);

    return (
        <div className={cn("relative overflow-hidden", className)}>
            <Image
                src={src || getLogoUrl(null, name)} // Ensure we always have a src
                alt={name}
                width={!isFill ? width : undefined}
                height={!isFill ? height : undefined}
                fill={isFill}
                className={cn("object-contain", imgClassName)}
                onError={handleError}
                unoptimized={unoptimized}
                priority={priority}
                sizes={sizes}
            />
        </div>
    );
}

'use client';

import { useMemo } from 'react';
import { formatDate } from '@/lib/utils';

interface TrafficData {
    date: string;
    views: number;
    clicks: number;
}

interface TrafficChartProps {
    data: TrafficData[];
    className?: string;
}

export function TrafficChart({ data, className }: TrafficChartProps) {
    // Generate last 7 days if data is empty or sparse, handled by parent ideally
    // But here we just render what we get.

    const maxValue = useMemo(() => {
        return Math.max(
            ...data.map(d => Math.max(d.views, d.clicks, 1)),
            10 // Minimum scale
        );
    }, [data]);

    const points = useMemo(() => {
        if (data.length === 0) return { views: '', clicks: '' };

        const width = 100; // SVG viewBox width
        const height = 50; // SVG viewBox height
        const step = width / (data.length - 1 || 1);

        const viewsPoints = data.map((d, i) => {
            const x = i * step;
            const y = height - (d.views / maxValue) * height;
            return `${x},${y}`;
        }).join(' ');

        const clicksPoints = data.map((d, i) => {
            const x = i * step;
            const y = height - (d.clicks / maxValue) * height;
            return `${x},${y}`;
        }).join(' ');

        return { views: viewsPoints, clicks: clicksPoints };

    }, [data, maxValue]);


    return (
        <div className={`w-full ${className}`}>
            <div className="flex items-center justify-between mb-4">
                <div className="flex items-center gap-6">
                    <div className="flex items-center gap-2">
                        <div className="h-3 w-3 rounded-full bg-purple-500" />
                        <span className="text-[10px] font-black uppercase tracking-widest text-slate-500">Article Views</span>
                    </div>
                    <div className="flex items-center gap-2">
                        <div className="h-3 w-3 rounded-full bg-blue-500" />
                        <span className="text-[10px] font-black uppercase tracking-widest text-slate-500">Tool Clicks</span>
                    </div>
                </div>
            </div>

            <div className="relative aspect-[3/1] w-full bg-slate-50 dark:bg-slate-900/50 rounded-2xl border border-slate-100 dark:border-slate-800 p-6 overflow-hidden">
                {/* SVG Chart */}
                <svg
                    viewBox="0 0 100 50"
                    preserveAspectRatio="none"
                    className="h-full w-full overflow-visible"
                >
                    {/* Grid Lines */}
                    {[0, 25, 50, 75, 100].map((y) => (
                        <line
                            key={y}
                            x1="0"
                            y1={50 - y / 2} // Scale 0-50 height
                            x2="100"
                            y2={50 - y / 2}
                            stroke="currentColor"
                            strokeWidth="0.1"
                            className="text-slate-200 dark:text-slate-800"
                            vectorEffect="non-scaling-stroke"
                        />
                    ))}

                    {/* Area Fills (Optional) */}

                    {/* Lines */}
                    <polyline
                        points={points.views}
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                        className="text-purple-500"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        vectorEffect="non-scaling-stroke"
                    />
                    <polyline
                        points={points.clicks}
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                        className="text-blue-500"
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        vectorEffect="non-scaling-stroke"
                    />
                </svg>

                {/* X-Axis Labels */}
                <div className="absolute bottom-2 left-6 right-6 flex justify-between text-[9px] font-black text-slate-400 uppercase tracking-widest pointer-events-none">
                    {data.map((d, i) => (
                        (i % Math.ceil(data.length / 5) === 0 || i === data.length - 1) ? (
                            <span key={d.date}>{new Date(d.date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}</span>
                        ) : null
                    ))}
                </div>
            </div>
        </div>
    );
}

